exports.Insert = function ( Model , CallBack ) {
    var Sql = "Insert Into ClubThread Set Thread_SN=?Thread_SN,Title=?Title,UserID=?UserID,UserName=?UserName,ModuleID=?ModuleID,LabelID=?LabelID,ThreadType=?ThreadType,GroupID=?GroupID,GroupLabelID=?GroupLabelID,ReadCount=?ReadCount,ReqCount=?ReqCount,AgreeCount=?AgreeCount,CollectionCount=?CollectionCount,ForWardCount=?ForWardCount,SupportCount=?SupportCount,NoSupportCount=?NoSupportCount,LastReqUserID=?LastReqUserID,LastReqUserName=?LastReqUserName,ThreadAble=?ThreadAble,IsRecommend=?IsRecommend,IsTop=?IsTop,CreateTime=?CreateTime,UpdateTime=?UpdateTime,ThreadContent=?ThreadContent,ThreadImg=?ThreadImg,ThreadProdID=?ThreadProdID,ThreadProdImg=?ThreadProdImg,ThreadProdType=?ThreadProdType ";
    client.Query ( Sql , Model , function ( err , Result ) {
        CallBack ( err , Result );
    } )
}

/**
 * 插入记录
 * @param ModelList 实体集合 ，Type:Object, 实例：[{key1:value1,key2:value2},{key1:value1,key2:value2}]
 * @param CallBack   回调函数Function（err,Result）
 * @constructor
 */
exports.InsertList = function ( ModelList , CallBack ) {
    var Sql = "Insert Into ClubThread Set Thread_SN=?Thread_SN,Title=?Title,UserID=?UserID,UserName=?UserName,ModuleID=?ModuleID,LabelID=?LabelID,ThreadType=?ThreadType,GroupID=?GroupID,GroupLabelID=?GroupLabelID,ReadCount=?ReadCount,ReqCount=?ReqCount,AgreeCount=?AgreeCount,CollectionCount=?CollectionCount,ForWardCount=?ForWardCount,SupportCount=?SupportCount,NoSupportCount=?NoSupportCount,LastReqUserID=?LastReqUserID,LastReqUserName=?LastReqUserName,ThreadAble=?ThreadAble,IsRecommend=?IsRecommend,IsTop=?IsTop,CreateTime=?CreateTime,UpdateTime=?UpdateTime,ThreadContent=?ThreadContent,ThreadImg=?ThreadImg,ThreadProdID=?ThreadProdID,ThreadProdImg=?ThreadProdImg,ThreadProdType=?ThreadProdType";
    client.GetConnection ( function ( err , connection ) {
        if ( err ) {
            CallBack ( err , null );
            return;
        }
        for ( var i = 0 ; i < ModelList.length ; i ++ ) {
            var Model = ModelList[0];
            client.Query ( connection , Sql , Model , function ( err ) {
                if ( err ) {
                    event.emit ( 'InsertFail' );
                    return;
                }
                if ( i == ModelList.length - 1 ) {
                    event.emit ( 'InsertSuccess' );
                }
            } )
        }
        event.on ( 'InsertSuccess' , function () {
            connection.release ();
            CallBack ( true , null );
        } )
        event.on ( 'InsertFail' , function () {
            connection.release ();
            CallBack ( false , null );
        } )
    } )


}

