/**
 * 插入记录
 * @param Model 实体 Type:Object, 实例：{key1:value1,key2:value2}
 * @param CallBack   回调函数Function（err,Result）
 * @constructor
 */
exports.Insert = function ( Model , CallBack ) {
    var Sql = "Insert Into SFJ Set SFJ_State=?SFJ_State,SFJ_Name=?SFJ_Name,SFJ_CreateTime=?SFJ_CreateTime,SFJ_UpdateTime=?SFJ_UpdateTime,SFJ_ProdCount=?SFJ_ProdCount,SFJSiteLabelID=?SFJSiteLabelID,SFJLikeCount=?SFJLikeCount,SFJ_LabelID=?SFJ_LabelID,SFJ_UserID=?SFJ_UserID,SFJ_D_Income=?SFJ_D_Income,SFJ_TempStyle=?SFJ_TempStyle,SFJ_EvalCount=?SFJ_EvalCount,SFJ_ForWardCount=?SFJ_ForWardCount,SFJ_Users=?SFJ_Users,SFJ_Remark=?SFJ_Remark,SFJFailContent=?SFJFailContent ";
    client.Query ( Sql , Model , function ( err , Result ) {
        CallBack ( err , Result );
    } )
}

/**
 * 插入记录
 * @param ModelList 实体集合 ，Type:Object, 实例：[{key1:value1,key2:value2},{key1:value1,key2:value2}]
 * @param CallBack   回调函数Function（err,Result）
 * @constructor
 */
exports.InsertList = function ( ModelList , CallBack ) {
    var Sql = "Insert Into SFJ Set SFJ_State=?SFJ_State,SFJ_Name=?SFJ_Name,SFJ_CreateTime=?SFJ_CreateTime,SFJ_UpdateTime=?SFJ_UpdateTime,SFJ_ProdCount=?SFJ_ProdCount,SFJSiteLabelID=?SFJSiteLabelID,SFJLikeCount=?SFJLikeCount,SFJ_LabelID=?SFJ_LabelID,SFJ_UserID=?SFJ_UserID,SFJ_D_Income=?SFJ_D_Income,SFJ_TempStyle=?SFJ_TempStyle,SFJ_EvalCount=?SFJ_EvalCount,SFJ_ForWardCount=?SFJ_ForWardCount,SFJ_Users=?SFJ_Users,SFJ_Remark=?SFJ_Remark,SFJFailContent=?SFJFailContent";
    client.GetConnection ( function ( err , connection ) {
        if ( err ) {
            CallBack ( err , null );
            return;
        }
        for ( var i = 0 ; i < ModelList.length ; i ++ ) {
            var Model = ModelList[0];
            client.Query ( connection , Sql , Model , function ( err ) {
                if ( err ) {
                    event.emit ( 'InsertFail' );
                    return;
                }
                if ( i == ModelList.length - 1 ) {
                    event.emit ( 'InsertSuccess' );
                }
            } )
        }
        event.on ( 'InsertSuccess' , function () {
            connection.release ();
            CallBack ( true , null );
        } )
        event.on ( 'InsertFail' , function () {
            connection.release ();
            CallBack ( false , null );
        } )
    } )


}

