exports.Insert = function ( Model , CallBack ) {
    var Sql = "Insert Into SiteProd Set ProdID=?ProdID,SellerNick=?SellerNick,ProdSiteLbel=?ProdSiteLbel,ProdName=?ProdName,ProdPrice=?ProdPrice,ProdPromotionPrice=?ProdPromotionPrice,IsBagmail=?IsBagmail,commission=?commission,commission_rate=?commission_rate,pic_url=?pic_url,Wtime=?Wtime,HotCount=?HotCount,LikeCount=?LikeCount,ShopType=?ShopType,Color=?Color,XMCount=?XMCount,MGCount=?MGCount,XMPoint=?XMPoint,MGPoint=?MGPoint,EvalCount=?EvalCount,ShareCount=?ShareCount,XYCount=?XYCount,QYJCount=?QYJCount,ForWardCount=?ForWardCount,IsNew=?IsNew,IsOutOfDate=?IsOutOfDate ";
    client.Query ( Sql , Model , function ( err , Result ) {
        CallBack ( err , Result );
    } )
}

/**
 * 插入记录
 * @param ModelList 实体集合 ，Type:Object, 实例：[{key1:value1,key2:value2},{key1:value1,key2:value2}]
 * @param CallBack   回调函数Function（err,Result）
 * @constructor
 */
exports.InsertList = function ( ModelList , CallBack ) {
    var Sql = "Insert Into SiteProd Set ProdID=?ProdID,SellerNick=?SellerNick,ProdSiteLbel=?ProdSiteLbel,ProdName=?ProdName,ProdPrice=?ProdPrice,ProdPromotionPrice=?ProdPromotionPrice,IsBagmail=?IsBagmail,commission=?commission,commission_rate=?commission_rate,pic_url=?pic_url,Wtime=?Wtime,HotCount=?HotCount,LikeCount=?LikeCount,ShopType=?ShopType,Color=?Color,XMCount=?XMCount,MGCount=?MGCount,XMPoint=?XMPoint,MGPoint=?MGPoint,EvalCount=?EvalCount,ShareCount=?ShareCount,XYCount=?XYCount,QYJCount=?QYJCount,ForWardCount=?ForWardCount,IsNew=?IsNew,IsOutOfDate=?IsOutOfDate";
    client.GetConnection ( function ( err , connection ) {
        if ( err ) {
            CallBack ( err , null );
            return;
        }
        for ( var i = 0 ; i < ModelList.length ; i ++ ) {
            var Model = ModelList[0];
            client.Query ( connection , Sql , Model , function ( err ) {
                if ( err ) {
                    event.emit ( 'InsertFail' );
                    return;
                }
                if ( i == ModelList.length - 1 ) {
                    event.emit ( 'InsertSuccess' );
                }
            } )
        }
        event.on ( 'InsertSuccess' , function () {
            connection.release ();
            CallBack ( true , null );
        } )
        event.on ( 'InsertFail' , function () {
            connection.release ();
            CallBack ( false , null );
        } )
    } )


}

