/**
 * 插入记录
 * @param Model 实体 Type:Object, 实例：{key1:value1,key2:value2}
 * @param CallBack   回调函数Function（err,Result）
 * @constructor
 */
exports.Insert = function ( Model , CallBack ) {
    var Sql = "Insert Into SiteUser Set UserEmail=?UserEmail,UserPwd=?UserPwd,IsActivate=?IsActivate,UserGender=?UserGender,UserRealName=?UserRealName,UserPingRealName=?UserPingRealName,UserCreatTime=?UserCreatTime,UserLoginTime=?UserLoginTime,UserCurMoney=?UserCurMoney,UserNeedPayMoney=?UserNeedPayMoney,UserPayMoney=?UserPayMoney,UserAllMoney=?UserAllMoney,arrears=?arrears,UserLev=?UserLev,TaobaoUserID=?TaobaoUserID,TaoBaoNick=?TaoBaoNick,ParentUserID=?ParentUserID,MyRelationCount=?MyRelationCount,RelationMeCount=?RelationMeCount,HeaderImg=?HeaderImg,UserSycWeiboLabel=?UserSycWeiboLabel ";
    client.Query ( Sql , Model , function ( err , Result ) {
        CallBack ( err , Result );
    } )
}

/**
 * 插入记录
 * @param ModelList 实体集合 ，Type:Object, 实例：[{key1:value1,key2:value2},{key1:value1,key2:value2}]
 * @param CallBack   回调函数Function（err,Result）
 * @constructor
 */
exports.InsertList = function ( ModelList , CallBack ) {
    var Sql = "Insert Into SiteUser Set UserEmail=?UserEmail,UserPwd=?UserPwd,IsActivate=?IsActivate,UserGender=?UserGender,UserRealName=?UserRealName,UserPingRealName=?UserPingRealName,UserCreatTime=?UserCreatTime,UserLoginTime=?UserLoginTime,UserCurMoney=?UserCurMoney,UserNeedPayMoney=?UserNeedPayMoney,UserPayMoney=?UserPayMoney,UserAllMoney=?UserAllMoney,arrears=?arrears,UserLev=?UserLev,TaobaoUserID=?TaobaoUserID,TaoBaoNick=?TaoBaoNick,ParentUserID=?ParentUserID,MyRelationCount=?MyRelationCount,RelationMeCount=?RelationMeCount,HeaderImg=?HeaderImg,UserSycWeiboLabel=?UserSycWeiboLabel";
    client.GetConnection ( function ( err , connection ) {
        if ( err ) {
            CallBack ( err , null );
            return;
        }
        for ( var i = 0 ; i < ModelList.length ; i ++ ) {
            var Model = ModelList[0];
            client.Query ( connection , Sql , Model , function ( err ) {
                if ( err ) {
                    event.emit ( 'InsertFail' );
                    return;
                }
                if ( i == ModelList.length - 1 ) {
                    event.emit ( 'InsertSuccess' );
                }
            } )
        }
        event.on ( 'InsertSuccess' , function () {
            connection.release ();
            CallBack ( true , null );
        } )
        event.on ( 'InsertFail' , function () {
            connection.release ();
            CallBack ( false , null );
        } )
    } )


}

