exports.CheckArgs = function ( types , args ) {
    //检测参数个数
    if ( types.length != args.length ) {
        return false;
    }
    //检测参数类型
    for ( var i = 0 ; i < args.length ; i ++ ) {
        if ( args[i].constructor != types[i] ) {
            return false;
        }
    }
    return true;
}